mapper.select:

SELECT member_id as id, name, email, password, role, reg_time, update_time
FROM member
WHERE member_id = #{id};

SELECT member_id as id, name, email, password, role, reg_time, update_time
FROM member
WHERE email = #{email}

mapper.update:
UPDATE member
SET name=#{updateParam.name},
    password=#{updateParam.password},
    update_time=#{updateParam.updateTime}
WHERE member_id = #{id}
